import { LangContext } from "./App";
import { useContext } from "react";
import React, { useState, useEffect } from 'react';
import photo from './image2.png';
export default function Game(props) {
    //sacamos el lang del context para traducir
    const lang = useContext(LangContext);
    // const [foto1, setFoto1] = useState();

    //al pulsar una tecla
    const handlePressKey = (event) => {
        if (event.key === "Enter") {
            props.enter();
        }
    }

    const handleChange = (event) => {
        props.responder(event.target.value);
    }


    return (
        <div>
            <img id="fpregunta" src={props.quiz.attachment.url == null ? photo : props.quiz.attachment.url} />
            {props.quiz.question}
            <br />
            <input type="text" id="respuesta" placeholder={lang.dictionary["Answer"]} value={props.answer} onKeyDown={handlePressKey} onChange={handleChange} ></input>
            <br />
            <div id="autor">
                {props.quiz.author.username == null ? "Anónimo" : props.quiz.author.username}
            </div>
            <div>
                <img id="fautor" src={props.quiz.author.photo.url == null ? photo : props.quiz.author.photo.url} />
            </div>
        </div>
    );
}
