import React, { useState, useEffect } from 'react';
import Game from './Game';
import Timer from './timer';
import { quizzes } from "./mock-data";
import { LangContext } from "./App";
import { useContext } from "react";
import photo from './image2.png';
import './mejoraCSS/Quiz.css';

export default function Quiz(props) {

    const timer = 60

    const lang = useContext(LangContext);
    const [score, setScore] = useState(0);
    const [currentQuiz, setCurrentQuiz] = useState(0);
    const [quizzess, setQuizzess] = useState(quizzes);
    const [finished, setFinished] = useState(0);
    const [answer, setAnswer] = useState("");
    const [ended, setEnded] = useState(0);
    const [correcto, setCorrecto] = useState(0);
    const [count, setCount] = useState(timer);
    const [flag, setFlag] = useState(true);

    function responder(text) {
        setAnswer(text);
    }

    function end(num) {
        if (num == 0 || num < 0) {
            setEnded(1);
        }
    }

    useEffect(() => {
        fetch("https://core.dit.upm.es/api/quizzes/random10wa?token=769e57d6fcb84b970fcc")
            .then(res => res.json())
            .then(json => { setQuizzess(json) })
        setTimeout(() => {
            setFlag(false);
        }, 500)
    }, []);

    function correct() {
        setScore(score + 1);
        setFinished(1);
        setAnswer("");
    }

    function comprobar() {
        if (finished == 0) {
            if (answer.trim().toLocaleLowerCase() == quizzess[currentQuiz].answer.trim().toLocaleLowerCase()) {
                correct();
            }
        }
    }

    function Siguiente() {
        if (currentQuiz != quizzess.length - 1) {
            setCurrentQuiz(currentQuiz + 1);
            setFinished(0);
            setAnswer("");
        }
    }

    function Previous() {
        setCurrentQuiz(currentQuiz - 1);
        setFinished(0);
        setAnswer("");
    }
    function Reset() {
        setScore(0);
        setAnswer("");
        setCurrentQuiz(0);
        setFinished(0);
        setEnded(0);
        setCount(timer)
    }

    //MEJORA OPCIONAL TECLA ENTER
    function teclaEnter() {
        if (finished == 0) {
            if (answer.trim().toLocaleLowerCase() == quizzess[currentQuiz].answer.trim().toLocaleLowerCase()) {
                correct();
                Siguiente();
            }
        }
    }

    if ((finished == 1 & currentQuiz == quizzess.length - 1) || (ended == 1)) {
        return (
            <div>
                {score}
                <h1>FIN</h1>
            </div>
        );
    }
    return (
        <div>
            <h1>Pagina de juegos</h1>
            {flag ? <img src={photo}></img> : <div className='general'>

                <div className='Score'>
                    {lang.dictionary["Score"]}
                    {score}
                </div>
                {answer}
                <nav className='buttons' roles="tablist">
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(0)}>1</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(1)}>2</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(2)}>3</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(3)}>4</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(4)}>5</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(5)}>6</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(6)}>7</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(7)}>8</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(8)}>9</button>
                    <button disabled={finished == 1 & currentQuiz == quizzess.length - 1} onClick={() => setCurrentQuiz(9)}>10</button>
                </nav>
                <div className='timer'>
                    <Timer end={end} count={count} setCount={setCount} />
                </div>
                <Game quiz={quizzess[currentQuiz]} teclaEnter={teclaEnter} responder={responder} finished={finished} answer={answer} comprobar={comprobar} Next={Siguiente} />
                <br />
                <button onClick={() => comprobar()}>
                    {lang.dictionary["Submit"]}
                </button>
                <button disabled={currentQuiz == 0} onClick={() => Previous()}>
                    {lang.dictionary["Previous"]}
                </button>
                <button disabled={currentQuiz == quizzess.length - 1} onClick={() => Siguiente()}>
                    {lang.dictionary["Next"]}
                </button>
                <button onClick={() => Reset()}>Reset</button>
            </div>}
        </div>
    );


}
